function product_specification_get_meta( $value ) {
    global $post;

    $field = get_post_meta( $post->ID, $value, true );
    if ( ! empty( $field ) ) {
        return is_array( $field ) ? stripslashes_deep( $field ) : stripslashes( wp_kses_decode_entities( $field ) );
    } else {
        return false;
    }
}

function product_specification_add_meta_box() {
    add_meta_box(
        'product_specification-product-specification',
        __( 'Product Specification', 'product_specification' ),
        'product_specification_html',
        'post',
        'normal',
        'high'
    );
}
add_action( 'add_meta_boxes', 'product_specification_add_meta_box' );

