function product_specification_save( $post_id ) {
    if ( defined( 'DOING_AUTOSAVE' ) && DOING_AUTOSAVE ) return;
    if ( ! isset( $_POST['product_specification_nonce'] ) || ! wp_verify_nonce( $_POST['product_specification_nonce'], '_product_specification_nonce' ) ) return;
    if ( ! current_user_can( 'edit_post', $post_id ) ) return;

    if ( isset( $_POST['product_specification_brand'] ) )
        update_post_meta( $post_id, 'product_specification_brand', esc_attr( $_POST['product_specification_brand'] ) );

    if ( isset( $_POST['product_specification_color'] ) )
        update_post_meta( $post_id, 'product_specification_color', esc_attr( $_POST['product_specification_color'] ) );

    if ( isset( $_POST['product_specification_size'] ) )
        update_post_meta( $post_id, 'product_specification_size', esc_attr( $_POST['product_specification_size'] ) );

    if ( isset( $_POST['product_specification_weight'] ) )
        update_post_meta( $post_id, 'product_specification_weight', esc_attr( $_POST['product_specification_weight'] ) );

    if ( isset( $_POST['product_specification_pressure'] ) )
        update_post_meta( $post_id, 'product_specification_pressure', esc_attr( $_POST['product_specification_pressure'] ) );

    if ( isset( $_POST['product_specification_temperature'] ) )
        update_post_meta( $post_id, 'product_specification_temperature', esc_attr( $_POST['product_specification_temperature'] ) );

    if ( isset( $_POST['product_specification_force'] ) )
        update_post_meta( $post_id, 'product_specification_force', esc_attr( $_POST['product_specification_force'] ) );

    if ( isset( $_POST['product_specification_country'] ) )
        update_post_meta( $post_id, 'product_specification_country', esc_attr( $_POST['product_specification_country'] ) );

    if ( isset( $_POST['product_specification_rpm'] ) )
        update_post_meta( $post_id, 'product_specification_rpm', esc_attr( $_POST['product_specification_rpm'] ) );

    if ( isset( $_POST['product_specification_dimension'] ) )
        update_post_meta( $post_id, 'product_specification_dimension', esc_attr( $_POST['product_specification_dimension'] ) );

    if ( isset( $_POST['product_specification_power'] ) )
        update_post_meta( $post_id, 'product_specification_power', esc_attr( $_POST['product_specification_power'] ) );

    if ( isset( $_POST['product_specification_warranty'] ) )
        update_post_meta( $post_id, 'product_specification_warranty', esc_attr( $_POST['product_specification_warranty'] ) );

    if ( isset( $_POST['product_specification_price'] ) )
        update_post_meta( $post_id, 'product_specification_price', esc_attr( $_POST['product_specification_price'] ) );

}
add_action( 'save_post', 'product_specification_save' );

